mapper.select:
select 
from sys_article_info


        and article_id = #{vo.articleId}


        and article_title like CONCAT('%', #{vo.articleTitle}, '%')


        and column_id = #{vo.columnId,jdbcType=VARCHAR}


        and tag_id = #{vo.tagId,jdbcType=VARCHAR}


        and DATE_FORMAT(create_time,"%Y-%m-%d") = DATE_FORMAT(#{vo.createTime},"%Y-%m-%d")


        and create_name = #{vo.createName,jdbcType=VARCHAR}


        and DATE_FORMAT(release_time,"%Y-%m-%d") = DATE_FORMAT(#{vo.releaseTime},"%Y-%m-%d")


        and release_name = #{releaseName,jdbcType=VARCHAR}


        and delete_status = #{vo.deleteStatus,jdbcType=INTEGER}


        and release_status = #{vo.releaseStatus,jdbcType=INTEGER}
